mapper.select:
select

from townjxh
where date_Forecast = #{dateForecast,jdbcType=TIMESTAMP}
and town_code = #{townCode,jdbcType=VARCHAR}

mapper.delete:
delete
from townjxh
where date_Forecast = #{dateForecast,jdbcType=TIMESTAMP}
  and town_code = #{townCode,jdbcType=VARCHAR}

mapper.select:
select *
from townjxh
where date_format(date_Forecast, '%Y-%m-%d') = #{time}
  AND town_code = #{code}

select ifnull((select min(T)
               from TownJXH
               where date_Forecast <= #{endDate}
                 and date_Forecast >= #{startDate}
                 and town_code = #{townCode}
                 and T > -50), 0.00) as minT,
       ifnull(max(T), 0.00)          as maxT,
       ifnull(avg(Rh), 0.00)         as avgRh
from TownJXH
where date_Forecast <= #{endDate}
  and date_Forecast >= #{startDate}
  and town_code = #{townCode}

select ifnull(min(Wind_S), 0.00) as minS,
       ifnull(max(Wind_S), 0.00) as maxS
from TownJXH
where date_Forecast <= #{endDate}
  and date_Forecast >= #{startDate}
  and town_code = #{townCode}

select *
FROM TownJXH
WHERE date_Forecast = #{date}
  AND town_code = #{townCode}

select Wind_D
from TownJXH
where date_Forecast <= #{endDate}
  and date_Forecast >= #{startDate}
  and town_code = #{townCode}
  and Wind_D is not null
